class Exopite_Anti_Spam_Icons {

    public $icons = array();
    public $count = 0;

    public function __construct() {

        $this->icons = $this->set_icons();
        // $this->count = count( $this->icons );

    }

    public function get_icons( $amount = 3 ) {

        // If somebody cheat.
        if ( ! $amount || empty( $amount ) || intval( $amount ) < 1 ) {
            $amount = 3;
        }

        $random = array_rand( $this->icons, $amount );
        $choices = array();
        foreach ( $random as $key => $value ) {
            $choices[$value] = $this->icons[$value];
        }
        return $choices;

    }

